case class Config(restricted: String, mailConfig:MailConfig)

object Config{

  lazy val default = Config("thrownforaloop.com",MailConfig("localhost",9000,"mike"))
}

case class MailConfig(
  host:String,
  port:Int,
  user:String
)

object Form{
  case class Field(name:String,value:String,onChange:ReactEventI=>Unit)


  val field = ReactComponentB[Field]("form-field")
    .render(P=>{
      val Field(name,value,onChange) = P
      <.div(^.`class`:="form-group",
        <.label(
          ^.`for` :=name,
          name.capitalize),
        <.input(
          ^.tpe := "text",
          ^.`class`:="form-control",
          ^.id := name,
          ^.value := value,
          ^.onChange ==> onChange
        ))
    })
    .build

  val form = ReactComponentB[Seq[Field]]("form")
    .render(P=>{
      <.div(^.`class`:="panel panel-default",
        <.div(^.`class`:="panel",
          <.div(^.`class`:="panel-heading",
            "Config Edit"
          ),
          <.div(^.`class`:="panel-body",
            <.form(
              P.map(f => field.withKey(f.name)(f))
            )
          )
        )
      )
    })
  .build

  // a static construction of our form
  def static() = {
    val Config(restricted,MailConfig(host,port,user)) = Config.default
    val noOp = (e:ReactEventI) => {}
    val fields = Seq(
        Field("restricted" ,restricted    ,noOp),
        Field("host"       ,host          ,noOp),
        Field("user"       ,user          ,noOp),
        Field("port"       ,port.toString ,noOp))

    form(fields)
  }
}

object ConfigForm{
  case class Props(onSubmit:Config=>Unit)
  case class State(config:Config)

  class Backend(t:BackendScope[Unit,State]){
    def modifyRestricted(e:ReactEventI) = {
      t.modState(
        s=>s.copy(
          config=s.config.copy(
            restricted=e.currentTarget.value)))
    }
    def modifyHost(e:ReactEventI) = {
      t.modState(
        s=>s.copy(
          config=s.config.copy(
            mailConfig=s.config.mailConfig.copy(
              host=e.currentTarget.value))))
    }
    def modifyUser(e:ReactEventI) = {
      t.modState(
        s=>s.copy(
          config=s.config.copy(
            mailConfig=s.config.mailConfig.copy(
              user=e.currentTarget.value))))
    }

  }

  val form = ReactComponentB[Unit]("config-form")
    .initialState(State(Config.default))
    .backend(new Backend(_))
    .render((P,S,B) =>{
      val Config(restricted,MailConfig(host,port,user)) = S.config
      val Field = Form.Field

      val fields = Seq(
        Field("restricted" ,restricted ,B.modifyRestricted),
        Field("host"       ,host       ,B.modifyHost),
        Field("user"       ,user       ,B.modifyUser))

      Form.form(fields)
    })
    .buildU

}

import os
import mercurial.commands

class HG:
  def __init__(self,ui,repo):
    self.ui = ui
    self.repo = repo
    self.files = self.getAddedFiles()

  def existsInRepo(self, filename):
    self.ui.pushbuffer()
    mercurial.commands.locate(self.ui,self.repo,include=[filename])
    files = self.ui.popbuffer().split("\n")

    for repoFile in files:
      if(pathsEqual(filename,repoFile)):
        return True

    return False

  def getAddedFiles(self):
    self.ui.pushbuffer()
    mercurial.commands.status(self.ui,self.repo,no_status=True, added = True)
    files = self.ui.popbuffer().split("\n")
    return files

  def filesExistInDir(self,dirname):
    self.ui.pushbuffer()
    mercurial.commands.locate(self.ui,self.repo,include=[dirname])
    files = self.ui.popbuffer().split("\n")
    files = filter(lambda x:x != "", files)
    return len(files) > 0

  def show(self, message):
    self.ui.status(message + "\n")

def precommit(ui,repo, **kwargs):
  hg = HG(ui,repo)
  clean = checkFiles(hg)
  return not clean;

def pathsEqual(a,b):
  return os.path.normcase(a) == os.path.normcase(b)


def checkFiles(hg):

  path = os.path.normcase("UnityProject/Assets/")

  result = True
  files = hg.files
  for singleFile in files:
    if singleFile == '':
      continue

    if not os.path.normcase(singleFile).startswith(path):
      continue

    isMetaFile = singleFile.endswith(".meta")

    filename = singleFile if not isMetaFile else singleFile.replace(".meta", "")
    metaFile = singleFile if isMetaFile else singleFile + ".meta"

    isFolder = os.path.isdir(os.path.normcase(filename))

    if isMetaFile:
      if isFolder:
        if not hg.filesExistInDir(filename):
          hg.show("Commiting a meta file'" + metaFile +
            "' for a directory which contains no files." )
          return False
      else:
        if not hg.existsInRepo(filename):
          hg.show("Commiting a meta file'" + metaFile +
            "' without adding associated file '" + filename + "'.")
          return False

    else:
      if not hg.existsInRepo(metaFile):
        hg.show("Committing a file '" + filename +
          "' without adding associated meta file '" + metaFile + "'.")
        return False


  return result
